import React, { useState } from "react";
import styles from "./AddComment.module.scss";
import TextField from "@mui/material/TextField";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import { a } from ''

export const AddComment = ({ onAddComment }) => {
  const [text, setText] = useState("");

  const handleSubmit = () => {
    if (text.trim()) {
      onAddComment(text); // Передаём текст комментария в родительский компонент или обработчик
      setText(""); // Очищаем поле ввода после отправки
    }
  };

  return (
    <div className={styles.root}>
      <Avatar
        classes={{ root: styles.avatar }}
        src="https://mui.com/static/images/avatar/5.jpg"
      />
      <div className={styles.form}>
        <TextField
          label="Написать комментарий"
          variant="outlined"
          maxRows={10}
          multiline
          fullWidth
          value={text}
          onChange={(e) => setText(e.target.value)} // Обновляем состояние при вводе текста
        />
        <Button
          variant="contained"
          onClick={handleSubmit} // Обработчик отправки комментария
        >
          Отправить
        </Button>
      </div>
    </div>
  );
};
