import React, { useState } from "react";
import { useEffect } from "react";
import { SideBlock } from "./SideBlock";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Divider from "@mui/material/Divider";
import List from "@mui/material/List";
import Skeleton from "@mui/material/Skeleton";
import { useDispatch } from "react-redux";
import { fetchComments, addComment, removeComment } from "../redux/slices/posts";

export const CommentsBlock = ({ postId, items, children, isLoading = true }) => {
  const dispatch = useDispatch()
  const [commentText, setCommentText] = useState("")

  React.useEffect(() => {
    if(isLoading) {
      dispatch(fetchComments())
    }
  }, [postId, dispatch, isLoading ])

  const handleAddComments = () => {
    if(commentText.trim()) {
      dispatch(addComment({postId, text: commentText}))
      setCommentText("")
    }
  }

  const handleRemoveComment = (commentId) => {
    dispatch(removeComment(commentId))
  }
  return (
    <SideBlock title="Комментарии"> 
      <List>
        {(isLoading ? [...Array(5)] : items).map((obj, index) => (
          <React.Fragment key={index}>
            <ListItem alignItems="flex-start">
              <ListItemAvatar>
                {isLoading ? (
                  <Skeleton variant="circular" width={40} height={40} />
                ) : (
                  <Avatar alt={obj.user.fullName} src={obj.user.avatarUrl} />
                )}
              </ListItemAvatar>
              {isLoading ? (
                <div style={{ display: "flex", flexDirection: "column" }}>
                  <Skeleton variant="text" height={25} width={120} />
                  <Skeleton variant="text" height={18} width={230} />
                </div>
              ) : (
                <ListItemText
                  primary={obj.user.fullName}
                  secondary={obj.text}
                />
              )}
            </ListItem>
            {
              !isLoading && (
                <button
                  onClick={handleRemoveComment(obj._id)}

                >
                  
                </button>
              )
            }
            <Divider variant="inset" component="li" />
          </React.Fragment>
        ))}
      </List>
      {children}
    </SideBlock>
  );
};
