import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchPostsByTag } from '../redux/slices/posts';
import { Post } from '../components/Post';

export const TagsPosts = () => {
  const { tag } = useParams(); // Получаем тег из URL
  const dispatch = useDispatch();
  const { posts } = useSelector((state) => state.posts); // Данные о постах
  const isPostsLoading = posts.status === 'loading'; // Проверка статуса загрузки


  useEffect(() => {
    if(tag)
    dispatch(fetchPostsByTag(tag)); // Загружаем посты по выбранному тегу
  }, [dispatch, tag]);

  return (
    <div>
      <h1>Посты с тегом: {tag}</h1>
      {isPostsLoading ? (
        <p>Загрузка...</p> // Отображаем текст "Загрузка..." пока данные не загружены
      ) : (
        posts.items.map((post) => (
          <Post
           key={post._id}
            title={post.title} text={post.text} tags={post.tags}  viewsCount={post.viewsCount} commentsCount={post.commentsCount} />
        ))
      )}
    </div>
  );
};
