import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { Post } from "../components/Post";
import { AddComment } from "../components/AddComment";
import { CommentsBlock } from "../components/comments/CommentsBlock";
import axios from "../axios";

export const FullPost = () => {
  const [data, setData] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const [comments, setComments] = useState([]);
  const [isCommentLoading, setIsCommentLoading] = useState(true);
  const { id } = useParams();
  const navigate = useNavigate();

  // Получаем данные текущего пользователя из Redux
  const userData = useSelector((state) => state.auth.data);

  useEffect(() => {
    if (!id) {
      console.warn("no ID");
    }
    axios
      .get(`/posts/${id}`)
      .then((res) => {
        setData(res.data);
        setIsLoading(false);
      })
      .catch((err) => {
        console.warn(err);
        alert("Ошибка при получении статьи");
      });
  }, [id]);

  useEffect(() => {
    if (!id) {
      console.warn("no ID");
    }
    axios
      .get(`/posts/${id}/comments`)
      .then((res) => {
        setComments(res.data);
        setIsCommentLoading(false);
      })
      .catch((err) => {
        console.warn(err);
        alert("Ошибка при получении комментариев");
      });
  }, [id]);

  const handleAddComment = async (text) => {
    if (!id) {
      console.warn("no ID");
    }
    try {
      const { data: newComment } = await axios.post(`/posts/${id}/comments`, {
        text,
      });
      setComments((prev) => [...prev, newComment]);
    } catch (err) {
      console.warn(err);
      alert("Ошибка при добавлении комментария");
    }
  };

  const handleEditPost = () => {
    navigate(`/posts/edit/${id}`);
  };

  if (isLoading) {
    return <Post isLoading={isLoading} isFullPost />;
  }

  return (
    <>
      <Post
        id={data._id}
        title={data.title}
        imageUrl={data.imageUrl ? `http://localhost:4444${data.imageUrl}` : ""}
        createdAt={data.createdAt}
        viewsCount={data.viewsCount}
        commentsCount={comments.length}
        tags={data.tags}
        user={data.user}
        isFullPost
      >
        <p>{data.text}</p>
        {userData?._id === data.user._id && ( // Показываем кнопку только для автора поста
          <button onClick={handleEditPost} style={{ marginTop: 10 }}>
            Редактировать пост
          </button>
        )}
      </Post>
      <CommentsBlock items={comments} isLoading={isCommentLoading}>
        <AddComment onAddComment={handleAddComment} />
      </CommentsBlock>
    </>
  );
};
