import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchComments, addComment, removeComment } from "../../redux/slices/posts";
import { SideBlock } from "./SideBlock";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Divider from "@mui/material/Divider";
import List from "@mui/material/List";
import Skeleton from "@mui/material/Skeleton";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";

export const CommentsBlock = ({ postId, items, children, isLoading = true }) => {
  const [commentText, setCommentText] = useState("");
  const dispatch = useDispatch();
  
  // Загружаем комментарии при монтировании компонента
  React.useEffect(() => {
    if (!isLoading) {
      dispatch(fetchComments(postId));
    }
  }, [dispatch, postId, isLoading]);

  const handleAddComment = () => {
    if (commentText.trim()) {
      dispatch(addComment({ postId, text: commentText }));
      setCommentText(""); // Очищаем поле ввода после добавления
    }
  };

  const handleRemoveComment = (commentId) => {
    dispatch(removeComment(commentId));
  };

  return (
    <SideBlock title="Комментарии">
      <List>
        {(isLoading ? [...Array(5)] : items).map((obj, index) => (
          <React.Fragment key={index}>
            <ListItem alignItems="flex-start">
              <ListItemAvatar>
                {isLoading ? (
                  <Skeleton variant="circular" width={40} height={40} />
                ) : (
                  <Avatar alt={obj.user.fullName} src={obj.user.avatarUrl} />
                )}
              </ListItemAvatar>
              {isLoading ? (
                <div style={{ display: "flex", flexDirection: "column" }}>
                  <Skeleton variant="text" height={25} width={120} />
                  <Skeleton variant="text" height={18} width={230} />
                </div>
              ) : (
                <ListItemText
                  primary={obj.user.fullName}
                  secondary={obj.text}
                />
              )}
            </ListItem>
            {!isLoading && (
              <Button
                onClick={() => handleRemoveComment(obj._id)}
                variant="outlined"
                color="error"
              >
                Удалить
              </Button>
            )}
            <Divider variant="inset" component="li" />
          </React.Fragment>
        ))}
      </List>

      {!isLoading && (
        <div style={{ marginTop: 20 }}>
          <TextField
            label="Добавить комментарий"
            variant="outlined"
            fullWidth
            multiline
            rows={3}
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
          />
          <Button
            onClick={handleAddComment}
            variant="contained"
            color="primary"
            style={{ marginTop: 10 }}
          >
            Отправить
          </Button>
        </div>
      )}

      {children}
    </SideBlock>
  );
};
