import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "../../axios";
import { fetchRemovePost } from "./auth";

export const fetchPosts = createAsyncThunk('posts/fetchPosts', async() => {
    const { data } = await axios.get('/posts')
    return data
})

export const fetchTags = createAsyncThunk('posts/fetchTags', async() => {
    const { data } = await axios.get('/tags')
    return data
})

export const fetchComments = createAsyncThunk('comments/fetchComments', async(postId) => {
    const {data} = await axios.get(`/posts/${postId}/comments`)
    return data
})

export const addComment = createAsyncThunk('comments/addComments', async(postId, text) => {
    const {data} = await axios.get(`/posts/${postId}/comments`, {text})
    return data
})


export const removeCom = createAsyncThunk('comments/addComments', async(postId, text) => {
    const {data} = await axios.get(`/posts/${postId}/comments`, {text})
    return data
})



const initialState = {
    posts: {
        items: [],
        status: 'loading'
    },
    tags: {
        items: [],
        status: 'loading'
    }
}

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {},
    extraReducers: {
        // Получение статей
        [fetchPosts.pending]: (state) => {
            state.posts.items = []
            state.posts.status = 'loading'
        },
        [fetchPosts.fulfilled]: (state, action) => {
            state.posts.items = action.payload
            state.posts.status = 'loaded'
        },
        [fetchPosts.rejected]: (state) => {
            state.posts.items = []
            state.posts.status = 'error'
        },
                // Получение тэгов
        [fetchTags.pending]: (state) => {
            state.tags.items = []
            state.tags.status = 'loading'
        },
        [fetchTags.fulfilled]: (state, action) => {
            state.tags.items = action.payload
            state.tags.status = 'loaded'
        },
        [fetchTags.rejected]: (state) => {
            state.tags.items = []
            state.tags.status = 'error'
        },
        // Удаление

        [fetchRemovePost.pending]: (state, aciton) => {
            state.posts.items = state.posts.items.filter(obj => obj._id !== aciton.meta.arg)
        }
    }
})

export const postsReducer = postsSlice.reducer
