import React from 'react';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import { Navigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { selectIsAuth } from '../../redux/slices/auth';
import { fetchAuth } from '../../redux/slices/auth';
import { useForm } from 'react-hook-form';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';

import styles from './Login.module.scss';

export const Registration = () => {
  const isAuth = useSelector(selectIsAuth)
  const dispatch = useDispatch()

  const{register, handleSubmit, setError, formState: {errors, isValid}} = useForm({
    defaultValues: {
      fullName: 'Haydar',
      email: 'haydar@haydar.ru',
      password: '12345'
    },
    mode: 'onChange'
  })

  const onSubmit = async(values) => {
    const data = await dispatch(fetchAuth(values))
  
  
      if(!data.payload) {
        return alert('Не удалось авторизоваться')
      }
      if('token' in data.payload) {
        window.localStorage.setItem('token', data.payload.token)
      }
    }
  
    if(isAuth) {
      return <Navigate to='/' />
    }
  
  return (
    <Paper classes={{ root: styles.root }}>
      <Typography classes={{ root: styles.title }} variant="h5">
        Создание аккаунта
      </Typography>
      <div className={styles.avatar}>
        <Avatar sx={{ width: 100, height: 100 }} />
      </div>
    <form>
      <TextField  
            error={Boolean(errors.fullName?.message)}
            helperText={errors.fullName?.message}
            {...register("fullName", {required: 'Полное Имя'})}
            className={styles.field} label="Полное имя"
            fullWidth
          />
        <TextField
          error={Boolean(errors.email?.message)}
          helperText={errors.email?.message}
          {...register("email", {required: 'Укожите почту'})}
          className={styles.field} label="E-Mail"
          fullWidth
          />
        <TextField
          error={Boolean(errors.password?.message)}
          helperText={errors.password?.message}
          {...register("password", {required: 'Укожите пароль'})}
          className={styles.field}
          label="Пароль"
          fullWidth 
          />
        <Button disabled={isValid} type='submit' size="large" variant="contained" fullWidth>
          Зарегистрироваться
        </Button>
    </form>
    </Paper>
  );
};
